for (auto it = mapLocalHost.begin(); it != mapLocalHost.end();) {
    localAddresses += it->first.ToString() + ":" + std::to_string(it->second.nPort);
    if (++it != mapLocalHost.end()) localAddresses += ", ";
}
if (localAddresses.empty()) localAddresses = "None";
