+ImportEntry *ImportDialog::addEntryMulti()
+{
+    ImportEntry *entry = new ImportEntry(ImportEntry::importMultiEntry, this);
+    entry->setModel(walletModel);
+    ui->entries->addWidget(entry);
+    connect(entry, &ImportEntry::removeMultiEntry, this, &ImportDialog::removeMultiEntry);
+
+    // Focus the field, so that entry can start immediately
+    //entry->clear();
+    entry->setFocus();
+    ui->scrollAreaWidgetContents->resize(ui->scrollAreaWidgetContents->sizeHint());
+    qApp->processEvents();
+    QScrollBar* bar = ui->scrollArea->verticalScrollBar();
+    if(bar)
+        bar->setSliderPosition(bar->maximum());
+
