+    if (fingerprint.standardVersion) {
+        Assert(tx.nVersion == TX_MAX_STANDARD_VERSION);
+    } else {
+        tx.nVersion = 1;
+    }
+
+    if (fingerprint.antiFeeSniping) {
+        // By default "Core" implements anti-fee-sniping (nLockTime == block_height - rng_fast.randrange(100))
