+        auto sortInputsBip69 = [](const CTxIn& a, const CTxIn& b) {
+            // COutPoint operator< does sort in accordance with Bip69, so just use that.
+            return a.prevout < b.prevout;
+        };
+        std::sort(tx.vin.begin(), tx.vin.end(), sortInputsBip69);
