+        auto sortOutputsBip69 = [](const CTxOut& a, const CTxOut& b) {
+            if (a.nValue == b.nValue) {
+                // Note: prevector operator< does NOT properly order scriptPubKeys lexicographically. So instead we
+                // fall-back to using std::memcmp.
